(19, 'sitemap', '["PlacesAPI"]', 'global $PlacesAr,$ContainerPlaces, $Response;\n\n$ContainerPlaces=Array();\n$TopLevel=Array();\n$Response=Array();\n\n$PlacesAr=$PlacesAPI;\n\nforeach ($PlacesAPI as $place_id=>$place){\n\n	if (isset($place["meta"]["parent"]) && ( $place["meta"]["parent"] != $place_id)  ) $ContainerPlaces[$place["meta"]["parent"]][]=$place_id;\n	elseif ( $place["meta"]["parent"] === $place_id) $TopLevel[]=$place_id;\n}\n\nfunction get_children($place_id){\n	global $PlacesAr, $ContainerPlaces, $Response;\n	if (isset($ContainerPlaces[$place_id])){\n		$Response[]="parent_open";\n		$Response[]="parent_line_open";\n		$Response[]=$PlacesAr[$place_id];\n		$Response[]="close";\n		$Results=array_map("get_children", $ContainerPlaces[$place_id]);\n		$Response[]="parent_close";\n		return $Results;\n	} else {\n		$Response[]="open";\n		$Response[]=$PlacesAr[$place_id];\n		$Response[]="close";\n		return 0;\n	}\n}\n$Results=array_map("get_children", $TopLevel);\nreturn Array("SequencedPlaces"=> $Response,"TopLevel"=> $TopLevel,"ContainerPlaces"=> $ContainerPlaces, "Places"=> $PlacesAPI);'),
(18, 'SitemapGenerator', '["PlacesAPI"]', '$ContainerPlaces = Array();\n$PlacesByPackage = Array();\n$PlacesByLevel = Array();\n$UniqueLevels = Array();\n$PlacesInOrder = Array();\n\n//global $ContainerPlaces, $PlacesByLevel;\nforeach ($PlacesAPI as $place_id=>$place){\n	$PlacesByPackage[$place[\\"app_GUID\\"]][$place_id]=$place;\n	if (isset($place[\\"parent\\"]) && ( $place[\\"parent\\"] != $place_id)  ) $ContainerPlaces[$place[\\"parent\\"]][$place_id]=$place;\n}\n\n\nforeach ($PlacesAPI as $place_id=>$place){\n\n	if (!isset($PlacesByLevel[$place_id])){ $PlacesByLevel[$place_id]=1;\n\n	foreach ($ContainerPlaces as $container_id=>$container)\n		if ($place_id == $container_id){\n			$PlacesByLevel[$container_id]=	1+$PlacesByLevel[$container_id];\nforeach ($ContainerPlaces[$place_id] as $sub_place_id=>$sub_place){\n\n\n$PlacesByLevel[$sub_place_id]=	2+$PlacesByLevel[$container_id];\n}\n}\n}\n}\n\n\n\n//$previous_place=\\"\\";\nforeach ($PlacesAPI as $place_id=>$place){\n\n	if (!isset($PlacesByLevel[$place_id])) \n$PlacesByLevel[$place_id]=1;\n	\n	foreach ($ContainerPlaces as $container_id=>$container)\n		foreach ($container as $sub_place_id=>$sub_place)\n			if ($place_id == $sub_place_id) $PlacesByLevel[$place_id]=	1+$PlacesByLevel[$container_id];\n\n$previous_place=$place_id;\n}\n\n\n\n\nforeach ($PlacesByLevel as $place_id=>$level){\n	if ($level===1) unset($PlacesByLevel[$place_id]);\n		//elseif ($level==2)  $PlacesByLevel[$place_id] = 0;\n		else \n$PlacesByLevel[$place_id] = $PlacesByLevel[$place_id]-1;\n\n	$PlacesAPI[$place_id][\\"level\\"]=$PlacesByLevel[$place_id];\n	\n	if (!in_array($place_id, $UniqueLevels ) && (($PlacesByLevel[$place_id]!=\\"\\")||($PlacesByLevel[$place_id]===0)) ) \n		$UniqueLevels[$PlacesByLevel[$place_id]][]=$place_id;\n}\n\n\n/*\n$PlacesByLevelUnmixed=Array();\nforeach( $PlacesByLevel as $place_id => $level){\n\n	if (isset($ContainerPlaces[$place_id])) {\n\n	$PlacesByLevelUnmixed[$place_id]=$level;\n\n		foreach( $ContainerPlaces[$place_id