$Sequenced_List);\n			$Sequenced_List[$poll]["name"]=$tag;\n			$Sequenced_List[$poll]["poll"]=$poll;\n			$Sequenced_List[$poll]["type"]=(isset($this_set["expression"]))?"expression":$this_set["movabl_type"];\n			$Sequenced_List[$poll]["content"]=(isset($this_set["expression"]))?$this_set["expression"]:$this_set["movabl_GUID"];\n\n		$Sequenced_List[]="close";\n		array_map("get_children", $temp_tag_ar);\n		$Sequenced_List[]="parent_close";\n	}else{\n		\n	$Known_Parents[count($Sequenced_List)]=$tag;\n	$Sequenced_List[]="parent_open";\n		$Sequenced_List[]="open";\n			$poll=count($Sequenced_List);			\n			$Sequenced_List[$poll]["name"]=$tag;\n			$Sequenced_List[$poll]["poll"]=$poll;\n			$Sequenced_List[$poll]["type"]=(isset($definition["expression"]))?"expression":$definition["movabl_type"];\n			$Sequenced_List[$poll]["content"]=(isset($definition["expression"]))?$definition["expression"]:$definition["movabl_GUID"];\n\n		$Sequenced_List[]="close";\n	$Sequenced_List[]="parent_close";\n	}\n}\n\n$Known_Parents =json_encode($Known_Parents);\n\nreturn Array("Sequenced_List"=>$Sequenced_List, "Known_Parents"=>$Known_Parents);'),
