(19, 'interface', 'text/html', '["Interface","SiteName","JSON_Content","Mapped","GUID"]', '<html>\n<script>\nrequestInProgress=0\nresponseDiv="statusInt"\n</script>\n\n<script src="http://{{SiteName}}///~/base.js"></script> \n\n\n<style>\n\nbody{font-family:sans-serif;}\na{text-decoration:none;}\n\ntextarea{height:30px;}\n#content{display:block; left:20px; width:60%; height:60%; overflow:auto;}\n#stausInt{position:absolute; bottom:3%; left:35%}\n#cta{position:absolute; bottom:3%; right:40%}\n.APISiteLibrary_Pattern{display:block; position:absolute; top:10%; left:62%; width:38%; height:85%;overflow:auto;}\n\n.APISiteLibrary_Pattern #media{display:block;}\n.APISiteLibrary_Pattern #function{display:block;}\n.APISiteLibrary_Pattern #interface{display:block;}\n#li_blank{display:none;}\n.hide{display:none;}\n.block{display:block;}\n\nol{border:dotted 1px gainsboro;}\nol div:hover{background:#efefef;}\n#ol_blank{display:none;}\n#expr_blank{display:none;}\n#link_blank{display:none;}\n</style>\n\n<script>\nvar JSON = JSON || {};\n\n// implement JSON.stringify serialization\nJSON.stringify = JSON.stringify || function (obj) {\n\n	var t = typeof (obj);\n	if (t != "object" || obj === null) {\n\n		// simple data type\n		if (t == "string") obj = ''"''+obj+''"'';\n		return String(obj);\n\n	}\n	else {\n\n		// recurse array or object\n		var n, v, json = [], arr = (obj && obj.constructor == Array);\n\n		for (n in obj) {\n			v = obj[n]; t = typeof(v);\n\n			if (t == "string") v = ''"''+v+''"'';\n			else if (t == "object" && v !== null) v = JSON.stringify(v);\n\n			json.push((arr ? "" : ''"'' + n + ''":'') + String(v));\n		}\n\n		return (arr ? "[" : "{") + String(json) + (arr ? "]" : "}");\n	}\n};\n\n\ncontent={{JSON_Content}};\nif (content==null)\ncontent = new Object();\n\n//alert(content);\nknown_parents_ar={{Mapped["Known_Parents"]}};\nif (known_parents_ar==null)\nknown_parents_ar= new Object();\n\n\n\nfunction getParents(id){\nparentNodes="";\ncollectedParents=Array();\nfor(i=0; i<100; i++){\n	this_parent=eval("seek(id,1)"+parentNodes+".id");\n	if (this_parent=="master_parent"){\n//alert(collectedParents);\n		return collectedParents;\n	}else{\n		collectedParents.push(this_parent);\n		parentNodes += ".parentNode";\n	}\n}\n\n}\n\n\nfunction knownParents(ar,type){\n\nthis_address=Array();\nfor(i in ar)\n	if(ar[i].substring(0,3)=="ol_") this_address.push(known_parents_ar[ar[i].substring(3)]);\n\nthis_address=this_address.reverse();\n\nfull_address="";\nfor(i in this_address){\n	if (i>0)full_address += ".tags."+this_address[i]\n	else full_address += "."+this_address[i]\n}\n//alert(full_address);\nreturn "content"+full_address;\n\n}\n\n\nfunction saveNode(addressed,content_id,type){\n//alert(addressed);\n	addressed=eval(addressed);\n	addressed[type]=seek(content_id,1).value;\n	seek("json",1).value=JSON.stringify(content);\n//alert( JSON.stringify(content) )\n	doRequest(''http://{{SiteName}}///~/Set/Interface/{{Interface["interface_GUID"]}}'',''statusInt'',''createUnitFormInterface'','''');\n}\n\n\n\nnext_id_num=0;\n\nfunction createNode(parent,add_child){\n	parent_element=seek(parent,1);\n	if (add_child) {\n		alert("add_child");\n	} else {\n//alert(parent_element.lastChild.previousSibling.id+" "+parent_element.lastChild.previousSibling.previousSibling.id);\n\nif (next_id_num==0){\n\nprev_id=parent_element.lastChild.previousSibling.id.substr(3);\nprev_id_as_num=parseInt(prev_id);\nnext_id_num=prev_id_as_num+10;\n\n}else{\nnext_id_num=next_id_num+10;\n}\n\n/*if (parent_element.lastChild.previousSibling.id==undefined && parent_element.lastChild.previousSibling.previousSibling.id!=parent_element.lastChild.previousSibling.id){\nprev_id=parent_element.lastChild.previousSibling.previousSibling.id.substr(3);\nprev_id_as_num=parseInt(prev_id);\nnext_id_num=prev_id_as_num+20;\n\n}else{\nprev_id=parent_element.lastChild.previousSibling.id.substr(3);\nprev_id_as_num=parseInt(prev_id);\nnext_id_num=prev_id_as_num+10;\n\n}*/\n\n\n//alert(next_id_num + " " + prev_id_as_num);\n		new_element= seek("ol_blank",1).cloneNode(true);\n\nnew_element.id="ol_"+next_id_num;\nnew_element.childNodes[1].id="tag_"+next_id_num;\n		parent_element.insertBefore(new_element);\n	}\n}\n\nfunction insertAtEnd(parent_node,type){\nnode_num=parseInt(parent_node.id.substr(3));\n//alert(parent_node.id);\nif (type=="expr"){\n\n		new_element= seek("expr_blank",1).cloneNode(true);\n\nnew_element.id="tag_"+(node_num+2)+"_content";\n\ntag_name=seek("tag_"+node_num,1).value;\nknown_parents_ar[node_num]=tag_name;\n//alert(content);\ncontent[tag_name]=new Object();\ncontent[tag_name].expression="";\ncontent[tag_name].meta=[];\n//alert(JSON.stringify(content));\nnew_element.childNodes[1].id="expression_"+(node_num+2);\nnew_element.childNodes[3].id="l_expression_"+(node_num+2);\n		parent_node.insertBefore(new_element);\n\n\n}else if(type="link"){\n\n\nnew_element= seek("link_blank",1).cloneNode(true);\n\nnew_element.id="tag_"+(node_num+2)+"_content";\n\ntag_name=seek("tag_"+node_num,1).value;\nknown_parents_ar[node_num]=tag_name;\ncontent[tag_name]=new Object();\ncontent[tag_name].movabl_type="";\ncontent[tag_name].movabl_GUID="";\ncontent[tag_name].meta=[];\n//alert(JSON.stringify(content));\n\nnew_element.childNodes[1].id="movabl_type_"+(node_num+2);\nnew_element.childNodes[3].id="l_movabl_type_"+(node_num+2);\n\nnew_element.childNodes[5].id="movabl_GUID_"+(node_num+2);\nnew_element.childNodes[7].id="l_movabl_GUID_"+(node_num+2);\n\n//new_element.childNodes[1].id="expression_"+(node_num+2);\n//new_element.childNodes[3].id="l_expression_"+(node_num+2);\n		parent_node.insertBefore(new_element);\n\n\n}\n\n}\n\n</script>\n\n<body>\n\n{{if GUID = "new"}}\n\n<h3>Name Your Interface</h3>\n<span id="statusInt"></span>\n<form id="createUnitFormInterface" name="createUnitFormInterface">\n<textarea name="GUID" id="GUID" style="display:none;">{{Interface["interface_GUID"]}}</textarea>\n<textarea name="label" id="label" >{{Interface["meta"]["label"]}}</textarea> <a href="