(22, 'APISiteLibrary_Pattern', 'text/html', '["Media","Functions","Interfaces","SiteName"]', '<div class="APISiteLibrary_Pattern">\n\n<div id="media">\n<b>Media</b> <a href="javascript:void(0);" style="display:inline; border:0; background:transparent; padding:0; margin:0;" onclick="parent.openDoc(''http://{{SiteName}}///~/Media/new'', ''New Media'') ">\nnew</a><br>\n<ul class="category">\n{{for Media as movabl_id:movabl}}\n\n<li class="media"><a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Media/{{movabl_id}}'', ''{{movabl["label"]}}'') ">\n{{movabl["label"]}}</a></li>\n{{endfor}}\n</ul>\n</div>\n\n\n<div id="function">\n<b>Functions</b> <a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Function/new'', ''New Function'') ">\nnew</a><br>\n<ul class="category">\n{{for Functions as movabl_id:movabl}}\n<li class="function"><a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Function/{{movabl_id}}'', ''{{movabl["label"]}}'') ">\n{{movabl["label"]}} </a></li>\n{{endfor}}\n</ul>\n</div>\n\n<div id="interface">\n<b>Interfaces</b><a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Interface/new'', ''New Interface'') ">\nnew</a><br>\n<ul class="category">\n{{for Interfaces as movabl_id:movabl}}\n\n<li class="interface"><a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Interface/{{movabl_id}}'', ''{{movabl["meta"]["label"]}}'') ">\n{{movabl["meta"]["label"]}}</a></li>\n{{endfor}}\n</ul>\n</div>\n\n\n</div>'),
(26, 'tabs', 'text/html', '["SiteName"]', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> \n<html> \n<head> \n	<title>Movabls</title> \n\n<link rel="stylesheet" type="text/css" href="http://{{SiteName}}///~/tabs.css"> \n<script src="http://{{SiteName}}///~/base.js"></script> \n<script src="http://{{SiteName}}///~/tabs.js"></script> \n\n</head> \n \n<body> \n \n \n<!-- tab bar/header:\n  movabl_anchor - the /// thing - it returns to the base document as defined in the iframe src\n  actionsList - the dropdown of available commands.  a <ul> using showOverflow & hideOverflow()  \n  cont0 - the template tab that''s cloned in openDoc\n  tray - the main tab well including the overflow dropdown\n  tabs - the specific div where tabs live, not including the overflow well\n  -->\n<div id="header">\n<span style="display:none"><span id="cont0"><a href="javascript:void(0);" onclick="closeDoc(this.id)"  id="cls0" class="closeTab" style="width:20px; padding:3px;" >X</a></span></span><div id="tray"><div id="tabs_bar"><a href="javascript:void(0);" onclick="switchDoc(this.id)" id="tab0" class="activeTab" onfocus="this.blur()" ><img src="http://media.movabls.com/movabls.png"></a><span id="tabs"></span><ul onMouseOver="showOverflow(this.id);" onMouseOut="hideOverflow(this.id);" class="menu" style="z-Index:10000" id="cycleTabsRight"><li id="name" >+</li></ul></div></div></div> \n \n \n \n<!-- main presentation space .  note the iframe src will be the default/base/background document-->\n<div id="window_presentation"><iframe id="doc0" class="activeDoc" src="http://{{SiteName}}///~/launchpad"> \n</iframe></div> \n \n \n \n<script language="JavaScript" type="text/javascript"> \n \n/*\n allows a tab to be created from a #anchor in the url.  for example:\n tabs.html#http://google.com\n will open google.com in a tab on load.\n */\n \nrawlocation= document.location.href;\nif (rawlocation.indexOf("#") > 0){\nlocAr=rawlocation.split("#");\nopenDoc(locAr[1],locAr[1] );\n}\n \n</script> \n</body> \n \n</html> \n'),
(27, 'tabscss', 'text/css', '', 'html{overflow:hidden;  }\n\n* {font-family:sans-serif; font-size:13px; }\n\nbody { font-size:.75em; font-family:sans-serif; background:#dedede; margin:0px; overflow:hidden;}\n\na{border:0; color:purple; text-decoration:none;}\na img {text-decoration:none; border:0;}\nul {text-indent:0px; padding:0px;  margin:0px; list-style:none;}\nul li{left:0px;text-indent:0px; padding:0px; margin:0px; list-style:none;}\n\n#globalstatus{background:yellow; width:40%;}\n\n\n\n#tab0{background:#363636; color:white; -moz-border-radius:6px;\n-webkit-border-radius:6px;\n margin:5px; width:60px; top:8px; height: 18px; padding-top:7px;}\n\n#tab0.activeTab{background:purple; }\n\n#header{position:absolute; top:0px; left:15px; height:35px; right:18px; z-Index:10000;}\n\n#tray{\nposition:absolute; top:0px; left:0px; height:35px; right:0px;\nz-Index:10032; background:transparent; }\n\n\n\n#tabs{position:absolute; top:15px; left:115px;right:30px; height:30px; overflow:hidden;}\n\n#tabs_bar{position:absolute; top:0px; left:0px; right:18px; display:block; height:30px; width:auto; z-Index:100003;}\n\n.inactiveTab{position:relative; display:block;  \nbackground:gainsboro; color:black; height:30px; width:120px; \npadding-left:20px; line-height:30px; float:left; padding-right:20px; margin-right:3px;\nborder:solid 0px gainsboro; font-weight:normal;\n-moz-border-radius-topright:4px;\n-webkit-border-top-right-radius:4px;\n-moz-border-radius-topleft:4px;\n-webkit-border-top-left-radius:4px;\n}\n.activeTab{position:relative;  float:left; display:block;\n background:white;color:#343434;height:30px; line-height:30px;\n width:auto; padding-left:20px; padding-right:20px; \n font-weight:bold;\n-moz-border-radius-topleft:4px;\n-webkit-border-top-left-radius:4px;\n }\n \n .closeTab{\n position:relative;  float:left; display:block;\n background:white;color:#343434;height:30px; line-height:30px;\n width:120px; padding-left:20px; padding-right:20px; \nmargin-right:3px;\ncolor:gainsboro;\n font-weight:bold;\n-moz-border-radius-topright:4px;\n-webkit-border-top-right-radius:4px;\n }\n \n.inactiveDoc{display:none;}\n.activeDoc{display:block; position:absolute; top:10px; }\n.inactiveCls{display:none;}\n\n#window_presentation{ overflow:auto; display:block; position:absolute; height:auto; width:auto; top:45px; left:15px; right:0px; bottom:15px;  border-right:solid 0px black; padding-top:0px; padding-left:0px; padding-right:0; background:white; z-Index:10;\n-moz-border-radius:4px;\n-webkit-border-radius:4px;\n\n-moz-border-radius-bottomleft:15px;\n-webkit-border-bottom-left-radius:15px;\n }\n#window_presentation iframe{ overflow:auto; height:97%; width:100%; border:0;}\n\n\n.menu_on{background:pink;}\n.list_sel {border:solid 1px gainsboro; background:white; color:#363636; width:100px;}\n\n.topnav{font-weight:bold; font-size:14px; letter-spacing:.5px; color:#363636;}\n\n#actionsList img{margin-right:10px;}\n\n#actionsList {position:absolute; top:17px; left:58px;  \ntext-align:center; font-size:12px; font-weight:normal; \ndisplay:block; height:26px; width:188px; overflow:hidden; line-height:35px; \npadding-bottom:5px; \npadding-top:0px; \n-moz-border-radius:5px;\n-webkit-border-radius:5px;\n-moz-border-radius-topright:5px;\n-webkit-border-top-right-radius:5px;\n-moz-border-radius-topleft:5px;\n-webkit-border-top-left-radius:5px;\n\n\nbackground:white url(http://media.movabls.com/downarrow.png) no-repeat fixed 230px 28px; \nborder:solid 1px #dedede; z-index:10000;}\n#actionsList li{text-align:left; padding-left:15px; display:block; }\n#actionsList li a:hover{background:gainsboro;color:#363636; display:block; height:100%; width:100%;}\n#actionsList li a{background:white; color:#363636;display:block; height:100%; width:100%;}\n#actionsList li a.first:hover{ background:gainsboro;color:#363636; display:block; height:100%; width:70%; float:left;  }\n#actionsList li a.first{ background:white; color:#363636;display:block; height:100%;  width:70%;float:left; }