(28, 'tabsjs', 'text/javascript', '', '/* \r\nclone\r\ncopies a div\r\ninputs: \r\n	nodeId = an element ID\r\n	parentNode = the parent element to attach the new div to\r\n	newNodeId = an optional element ID to use for the resulting element\r\n*/\r\nfunction clone(nodeId,parentNode,newNodeId){\r\nnewNode=seek(nodeId,1).cloneNode(true);\r\nnewNode.id=(newNodeId!="")?newNodeId:nodeId+Math.round(Math.random()*1000);\r\nnewNode.innerHTML=newNode.id;\r\nseek(parentNode,1).appendChild(newNode); \r\nreturn newNode.id;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n/* \r\nopenDoc\r\ncreates a tab for a given path\r\n	path = a URL to load into the destination frame\r\n	title = a string title to display on the tab\r\n*/\r\n\r\n\r\nfunction openDoc(path, title){\r\nnewDocId=Math.round(Math.random()*1000);\r\nclone(''cont0'',''tabs'',''cont''+newDocId);\r\nseek(''cont''+newDocId,1).innerHTML="";\r\n\r\nclone(''tab0'',''cont''+newDocId,''tab''+newDocId);\r\nclone(''cls0'',''cont''+newDocId,''cls''+newDocId);\r\n\r\nclone(''doc0'',''window_presentation'',''doc''+newDocId);\r\n\r\nswitchDoc(newDocId);\r\nseek(''tab''+newDocId,1).innerHTML=title;\r\nseek(''cls''+newDocId,1).innerHTML="X";\r\n\r\nseek(''doc''+newDocId,1).src=path;\r\n\r\nleftTabs=new Array();\r\nrightTabs=new Array();\r\nshownTabs=new Array();\r\nhcnt=0;\r\nscnt=0;\r\nlcnt=0;\r\n\r\nallInTabs=seek("tabs",1).childNodes;\r\nallInList=seek("cycleTabsRight",1).childNodes;\r\nbarWidth=parseInt(seek("tabs",1).offsetWidth);\r\ncurrWidth=0;\r\nlisted=0;\r\nexposed=0;\r\n\r\nallInTabsAr=new Array();\r\nallInListAr=new Array();\r\nfor(j=0; j<allInTabs.length; j++)allInTabsAr[j]=allInTabs[j];\r\nfor(j=0; j<allInList.length; j++) if ((allInList[j].id !=undefined)&&(allInList[j].id.substring(0,6) =="l_cont")) allInListAr[j]=allInList[j];\r\n\r\n\r\nalltogether=allInTabsAr.concat(allInListAr);\r\n\r\nfor(j=0; j<allInListAr.length; j++){\r\nif ((allInListAr[j]!=undefined)&&(allInListAr[j].id!=undefined)){\r\ntabChildren=seek(allInListAr[j].id,1).childNodes;\r\nfor(r=0; r<tabChildren.length; r++) {\r\n\r\ntabChildren[r].parentNode.removeChild(tabChildren[r]);\r\n\r\n}\r\nseek("cycleTabsRight",1).removeChild(allInListAr[j]);\r\n}\r\n}\r\n\r\n\r\nfor(j=0; j<alltogether.length; j++){\r\n		currWidth +=150;\r\n		if (currWidth+30>=barWidth){\r\n		listed++;\r\nnewli=''l_''+alltogether[j].id;\r\nclone("name",''cycleTabsRight'',newli);\r\nseek(newli,1).innerHTML="";\r\n\r\nseek(newli,1).className="listedCont";\r\n\r\ntabChildren=seek(alltogether[j].id,1).childNodes;\r\nfor(r=0; r<tabChildren.length; r++){\r\nclone(tabChildren[r].id,newli,''l_''+tabChildren[r].id);\r\nseek(''l_''+tabChildren[r].id,1).innerHTML=seek(tabChildren[r].id,1).innerHTML;\r\nseek(''l_''+tabChildren[r].id,1).className="listed";\r\n\r\n\r\n}\r\n\r\n		}else{\r\n		exposed++;\r\n		}\r\n}\r\n\r\n\r\n\r\n}\r\n\r\n\r\n\r\n/*\r\nswitchDoc\r\nmakes a given document active/visible\r\n	docId= the document to be made active.\r\n*/\r\nfunction switchDoc(docID){\r\nidNum=docID;\r\n\r\nif (isNaN(parseInt(idNum) )){\r\n\r\nfirst=docID.substr(0,3);\r\nif ((first=="tab")||(first=="doc")) idNum=parseInt(docID.substr(3));\r\nif (first=="l_t") idNum=parseInt(docID.substr(5));\r\n\r\n}else{\r\nidNum=parseInt(idNum)\r\n}\r\nx=seek("window_presentation",1).childNodes;\r\nfor (i=0;i<x.length;i++)\r\n{\r\ncurrid=parseInt(x[i].id.substr(3));\r\nseek("tab"+currid, 1).className="inactiveTab";\r\nseek("cls"+currid, 1).className="inactiveCls";\r\n\r\nseek("doc"+currid, 1).className="inactiveDoc";\r\n\r\n}\r\nseek("tab"+idNum, 1).className="activeTab";\r\nseek("cls"+idNum, 1).className="closeTab";\r\n\r\nseek("doc"+idNum, 1).className="activeDoc";\r\n}\r\nfunction closeDoc(docID){\r\nidNum=docID;\r\n\r\n\r\n\r\nif (isNaN(parseInt(idNum) )){\r\n\r\nfirst=docID.substr(0,3);\r\nif ((first=="tab")||(first=="doc")||(first=="cls")) idNum=parseInt(docID.substr(3));\r\nif (first=="l_c") idNum=parseInt(docID.substr(5));\r\n}else{\r\nidNum=parseInt(idNum)\r\n}\r\n\r\n\r\nseek("doc"+idNum,1).parentNode.removeChild(seek("doc"+idNum,1));\r\nseek("tab"+idNum,1).parentNode.removeChild(seek("tab"+idNum,1));\r\nseek("cls"+idNum,1).parentNode.removeChild(seek("cls"+idNum,1));\r\nseek("cont"+idNum,1).parentNode.removeChild(seek("cont"+idNum,1));\r\n\r\n//seek("l_tab"+idNum,1).parentNode.removeChild(seek("l_tab"+idNum,1));\r\n//seek("l_cls"+idNum,1).parentNode.removeChild(seek("l_cls"+idNum,1));\r\n//seek("l_cont"+idNum,1).parentNode.removeChild(seek("l_cont"+idNum,1));\r\n\r\n\r\n\r\nswitchDoc("doc"+0);\r\n}\r\n\r\n\r\n/*\r\nshowOverflow\r\nused to show the full height of a semi-hidden <ul> (for the drop downs/overflow list) \r\n*/\r\n\r\nfunction showOverflow(id) {\r\n	seek(id,0).height = ''auto'';\r\n	if (id=="cycleTabsRight") 	seek(id,0).width = ''150px'';\r\n	\r\n}\r\n\r\n/*\r\nhideOverflow\r\nused to semi-hide a <ul> (for the drop downs/overflow list) \r\n*/\r\nfunction hideOverflow(id) {\r\n	seek(id,0).height = ''26px'';\r\n	if (id=="cycleTabsRight") 	seek(id,0).width = ''30px'';\r\n}'),
(29, 'basejs', 'text/javascript', '', 'function seek(ele,type){\r\nif (ele=document.getElementById(ele)) return (type) ? ele:ele.style; else return false;}\r\n\r\nfunction stateChange(a){\r\n	if (xmlhttp.readyState==4){\r\n		 if (xmlhttp.status==200){\r\n			response=xmlhttp.responseText;\r\n			scriptstartpos=response.indexOf("script>");\r\n			scriptendpos=response.indexOf("/script>")\r\n			loadscript="";\r\n//alert(response);\r\n			if (scriptendpos>0)\r\n				loadscript=response.substring(scriptstartpos+7, scriptendpos-1);\r\n\r\n			stylestartpos=response.indexOf("style>");\r\n			styleendpos=response.indexOf("/style>")\r\n\r\n			loadstyle="";\r\n			if (styleendpos>0)\r\n				loadstyle=response.substring(stylestartpos+7, styleendpos-1);\r\n\r\n			var head = document.getElementsByTagName("head")[0];\r\n			if (loadscript !=""){\r\n				script = document.createElement(''script'');\r\n				script.id = ''jstem'';\r\n				script.type = ''text/javascript'';\r\n				script.text= loadscript;\r\n				head.appendChild(script);\r\n			}\r\n			\r\n			if (loadstyle !=""){\r\n				var style = document.createElement("style");\r\n				style.setAttribute("type", "text/css");\r\n				if(style.styleSheet){// IE\r\n					style.styleSheet.cssText = loadstyle ;\r\n				} else {\r\n					var cssText = document.createTextNode(loadstyle);\r\n					style.appendChild(cssText);\r\n				}\r\n   			head.appendChild(style);\r\n			}	\r\n			if(responseDiv.substring(0,5)=="form_") seek(responseDiv,1).value=response;	\r\n			else if(responseDiv!="") seek(responseDiv,1).innerHTML=response;	\r\n			requestInProgress=0;\r\n		}  else  {\r\n			requestInProgress=2;\r\n	 	} \r\n	} \r\n}\r\n	\r\n\r\n\r\n\r\nfunction doRequest(URL,destDiv,formId,parentId){\r\n	if(destDiv=="")responseDiv=destDiv;\r\n	var boundaryString = ''AaB03x'';\r\n	var boundary = ''--'' + boundaryString;\r\n\r\n	sendStr = [\r\n	boundary\r\n	].join(''\\r\\n'');\r\n\r\n		\r\n	if ((formId!=undefined)&&(formId!="")){\r\n\r\n		if (parentId!="") formChildrenAr=seek(parentId,1).contentWindow.document.getElementById(formId).childNodes;\r\n		else formChildrenAr=seek(formId,1).childNodes;\r\n\r\n		strCnt=0;\r\n		for(j=0; j<formChildrenAr.length; j++) {\r\n\r\n			if ((formChildrenAr[j].name!=undefined)&&(formChildrenAr[j].name!="")) {\r\n//if ((formChildrenAr[j].type !="radio" )&&(formChildrenAr[j].checked !=0)) {\r\n//alert(sendStr);\r\n					strCnt++;\r\n					sendStr+=''\\r\\n'';\r\n					sendStr+=''Content-Disposition: form-data; name="''+formChildrenAr[j].name+''"'';\r\n					sendStr+=''\\r\\n'';\r\n					sendStr+=''\\r\\n'';\r\n					sendStr+=formChildrenAr[j].value;\r\n					sendStr+=''\\r\\n'';\r\n					sendStr+=boundary;\r\n\r\n				}\r\n//			}\r\n		}\r\n\r\n\r\n	}\r\n\r\n	if (requestInProgress==1) {\r\n		setTimeout("doRequest(''"+URL+"'',''"+destDiv+"'',''"+formId+"'',''"+id+"'',''"+parentId+"'')", 10);\r\n		return false;\r\n	}\r\n		\r\n	if (window.XMLHttpRequest) {\r\n\r\n		xmlhttp=new XMLHttpRequest()\r\n		if (xmlhttp){\r\n			xmlhttp.onreadystatechange=stateChange\r\n			requestInProgress=1;\r\n			xmlhttp.open("POST",URL,true)\r\n			\r\n			xmlhttp.setRequestHeader(''Content-Type'',''multipart/form-data; boundary='' + boundaryString);\r\n			//xmlhttp.setRequestHeader("Content-length", sendStr.length);\r\n			//xmlhttp.setRequestHeader("Connection", "close");\r\n			\r\n			xmlhttp.send(sendStr);\r\n		}\r\n		\r\n	}else if (window.ActiveXObject) {\r\n		xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")\r\n		if (xmlhttp){\r\n			xmlhttp.onreadystatechange=stateChange\r\n			xmlhttp.open("POST",URL,true)\r\n\r\n			xmlhttp.setRequestHeader(''Content-Type'',''multipart/form-data; boundary='' + boundaryString);\r\n			//xmlhttp.setRequestHeader("Content-length", sendStr.length);\r\n			//xmlhttp.setRequestHeader("Connection", "close");\r\n\r\n			xmlhttp.send(sendStr);\r\n\r\n		}\r\n	}\r\n}\r\n	 \r\n	 \r\n	 \r\nfunction ajaxSubmitForm(pass1,pass2,parentId){\r\nvar d = new Date();\r\nvar t = d.getTime();\r\ntim=t+"";\r\ntimestamp=tim.substr(0,6);\r\n	if (pass1!="update"){\r\n		doRequest("ajaxSubmission/"+pass1+"/"+pass2, pass1+pass2+"Status",pass1+pass2+"Form",parentId)\r\n	}else{\r\n		doRequest("update/"+pass2, "globalstatus",pass1+pass2+"Form",parentId)\r\n	}\r\n}\r\n\r\nfunction createCookie(name,value,days) {\r\n	if (days) {\r\n		var date = new Date();\r\n		date.setTime(date.getTime()+(days*24*60*60*1000));\r\n		var expires = "; expires="+date.toGMTString();\r\n	}\r\n	else var expires = "";\r\n	document.cookie = name+"="+value+expires+"; path=/";\r\n}\r\n\r\nfunction readCookie(name) {\r\n	var nameEQ = name + "=";\r\n	var ca = document.cookie.split('';'');\r\n	for(var i=0;i < ca.length;i++) {\r\n		var c = ca[i];\r\n		while (c.charAt(0)=='' '') c = c.substring(1,c.length);\r\n		if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);\r\n	}\r\n	return null;\r\n}\r\n\r\nfunction eraseCookie(name) {\r\n	createCookie(name,"",-1);\r\n}'),
