(41, 'Sitemapper', 'text/html', '["Places","SiteName"]', '<div id="Sitemap">\n\n<style>\n\n\n\n#field ol {  margin:5px; padding:3px; display:block;position:relative; margin-top:40px; clear:none; float:left;  text-align:left; height:auto; width:auto; background:none; border:dotted 1px white; -moz-border-radius:6px;\n-webkit-border-radius:6px;}\n\n\n#field ol li.parent {text-weight:bold; margin:0; padding:0;  float:left; clear:none; display:block; position:relative; text-align:left; height:auto; width:auto; background:none;}\n\n#field ol li {margin:3px; padding:0; display:block;  margin-top:0px;  position:relative; float:right; text-align:left; height:auto; width:120px; background:none;}\n\n#field ol ol li {margin:3px; padding:0; display:block;  margin-top:0px;  position:relative; float:right; text-align:left; height:auto; width:120px; background:none;}\n\n#field ol ol ol li {margin: 3px; padding:0; display:block;  margin-top:40px;  position:relative; float:left; text-align:left; height:auto; width:120px; background:none;}\n\n#field ol ol ol ol li {margin: 3px; padding:0; display:block;  margin-top:0px;  position:relative; clear:both; text-align:left; height:auto; width:120px; background:none;}\n\n\n\n\n#field li.place{border:solid 4px white; height:auto; -moz-border-radius:6px;\n-webkit-border-radius:6px; padding:10px; background:white; }\n#field li.place:hover{border:solid 1px #efefef; background:gainsboro; padding:4px; height:auto;}\n\n#field li.parent{border:solid 4px white; height:auto; -moz-border-radius:6px;\n-webkit-border-radius:6px; padding:10px;  background:white;}\n#field li.parent:hover{border:solid 1px white; background:Khaki;padding:4px; height:auto;}\n\nol ol:hover{border:solid 1px #fafafa; background:#efefef;}\n\n#map{text-align:center;  background:black; height:auto; width:auto;}\n\n\n#field{position:relative; height:auto; width:auto; margin:auto; clear:both; white-space:nowrap;}\n\nli.place .meta{display:none;}\nli.place:hover .meta{display:block; width:100px; height:100px; background:white;}\n\nli.parent .meta{display:none;}\nli.parent:hover .meta{display:block; width:100px; height:100px;background:white;}\n.open {background:red;}\n.close{background:green;}\n\n</style>\n<!-- <a href="javascript:void(0);" onclick="seek(parent.id,1).className=(seek(parent.id,1).className==''open'')?''closed'':''open'';">tog<a> -->\n<div id="map">\n<div id="field">\n<ol>\n{{for Places["SequencedPlaces"] as place_order_id:place_line}}\n\n{{if place_line = "parent_open"}}<ol id="ol_{{place_order_id}}">{{endif}}\n{{if place_line = "parent_line_open"}}<li class="parent">{{endif}}\n{{if place_line = "open"}}<li class="place">{{endif}}\n{{if place_line != "open" and place_line != "close" and place_line != "parent_open"  and place_line != "parent_line_open" and place_line != "parent_close" }}\n{{place_line["meta"]["label"]}}<br><a href=''http://{{SiteName}}{{place_line["url"]}}'' target="_blank"> \n{{place_line["url"]}}</a><span class="meta">\n<a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Media/{{place_line["media_GUID"]}}'', ''{{place_line["media_GUID"]}}'')">{{place_line["media_GUID"]}}</a><br>\n<a href="javascript:void(0);" onclick="parent.openDoc(''http://{{SiteName}}///~/Interface/{{place_line["interface_GUID"]}}'', ''{{place_line["interface_GUID"]}}'')">{{place_line["interface_GUID"]}}</a></span>\n{{endif}}\n{{if place_line = "close"}}</li>{{endif}}\n{{if place_line = "parent_close"}}</ol>{{endif}}\n{{endfor}}\n</ol>\n</div>\n</div>\n</div>\n');
