r\n}\r\n\r\na {\r\ncursor:pointer;\r\n}\r\n\r\nbutton {\r\ncursor:pointer;\r\npadding:3px 8px;\r\n}\r\n\r\nlabel {\r\ndisplay:block;\r\npadding-bottom:7px;\r\n}\r\n\r\nh1 {\r\nmargin:0;\r\npadding:0;\r\n}\r\n\r\nimg {\r\nborder:none;\r\n}\r\n\r\ninput {\r\nvertical-align:middle;\r\n}\r\ninput.text {\r\npadding:2px;\r\nmargin-bottom:12px;\r\n}\r\ntextarea {\r\npadding:2px;\r\nmargin-bottom:12px;\r\n}\r\nselect {\r\nmargin-bottom:12px;\r\nmin-width:150px;\r\n}\r\n\r\ntable {\r\nwidth:100%;\r\n}\r\ntable, tr, th, td {\r\nborder-collapse:collapse;\r\npadding:0;\r\nmargin:0;\r\ntext-align:left;\r\n}\r\n\r\n/*---- Style Rules ----*/\r\n\r\nbody {\r\nbackground:#ffffff;\r\n}\r\n\r\n.ui-widget {\r\nfont-size:12px !important;\r\n}\r\n\r\na {\r\ncolor:#3ec1f2;\r\ntext-decoration:underline;\r\n}\r\na:hover {\r\ntext-decoration:none;\r\n}\r\n\r\nh1 {\r\nfont-size:22px;\r\n}\r\n\r\ninput {\r\nfont:12px Arial, Helvetica, sans-serif;\r\ncolor:#666666;\r\n}\r\n\r\nlabel {\r\nfont-weight: bold;\r\n}\r\n\r\ntextarea {\r\nfont-size:12px;\r\ncolor:#666666;\r\n}\r\n\r\ntable.data th, \r\ntable.data td {\r\npadding:5px;\r\n}\r\n\r\ntable.data th {\r\ntext-align:left;\r\nfont-weight:bold;\r\ncolor:#ffffff;\r\nbackground:#aaaaaa;\r\n}\r\n\r\n.secondary-content {\r\nbackground:#eeeeee;\r\n}\r\n\r\n#flash_message {\r\ncolor:#ffffff;\r\nfont-weight:bold;\r\n}\r\n.flash-message-confirmation {\r\nbackground:#87d5ad;\r\nborder:1px solid #02a146;\r\n}\r\n.flash-message-notice {\r\nbackground:#a1e1f9;\r\nborder:1px solid #12b0f0;\r\n}\r\n.flash-message-error {\r\nbackground:#f79497;\r\nborder:1px solid #ee242b;\r\n}\r\n\r\n#tooltip {\r\nbackground:#ffffe3;\r\nborder:1px solid #000000;\r\n}\r\n\r\n#faux_inputs_field {\r\nborder:1px solid #999999;\r\nbackground:#ffffff;\r\nheight:15px;\r\n}\r\n\r\n#toolbar {\r\nline-height:18px;\r\n}\r\n\r\n/*---- Tabs ----*/\r\n\r\n#window_header{\r\nwidth:100%;\r\nheight:35px;\r\noverflow:hidden;\r\npadding:0;\r\n}\r\n\r\n#movabls_logo {\r\nposition:relative;\r\nfloat:left;\r\nheight:27px;\r\nmargin:4px 1px 0 1px;\r\npadding:0px 10px;\r\noverflow:hidden;\r\n}\r\n\r\n#movabls_logo img {\r\nposition:relative;\r\n}\r\n\r\n#window_tab_container {\r\nmargin:0;\r\npadding:0;\r\n}\r\n\r\n#window_header .window_tab {\r\ndisplay:block;\r\nfloat:left;\r\nposition:relative;\r\nmargin:5px 2px 0 2px;\r\npadding:5px 30px 5px 8px; \r\nheight:20px;\r\ncursor:pointer;\r\n}\r\n\r\n#window_header .window_tab .window_tab_close {\r\nposition:absolute;\r\ntop:4px;\r\nright:5px;\r\n}\r\n\r\n#window_container {\r\nposition:absolute;\r\ntop:35px;\r\nleft:0px;\r\nright:0px;\r\nbottom:0px;\r\nwidth:auto;\r\nheight:auto;\r\n}\r\n\r\n.window_content {\r\nposition:absolute;\r\ntop:0px;\r\nleft:0px;\r\nwidth:100%;\r\nheight:100%;\r\nborder:none;\r\n}\r\n\r\n.dialog-save-toolbar {\r\npadding-top:15px;\r\ntext-align:right;\r\n}\r\n\r\n.table-label {\r\nfloat:left;\r\n}\r\n.table-toolbar {\r\nfloat:right;\r\nmargin-bottom:5px;\r\n}\r\n\r\n.key-value-table td {\r\nvertical-align:top;\r\n}\r\n.key-value-table input,\r\n.key-value-table select {\r\nmargin-top:1px;\r\nwidth:95%;\r\n}\r\n.key-value-table textarea {\r\nwidth:95%;\r\nheight:40px;\r\n}\r\n.key-value-create-key {\r\ncolor:#0000ff;\r\ntext-decoration:underline;\r\n}\r\n\r\n/* Individual Pages */\r\n\r\n#content {\r\nposition:absolute;\r\ntop:0;\r\nleft:0;\r\nbottom:0;\r\nright:0;\r\nwidth:auto;\r\nheight:auto;\r\nborder:none;\r\n}\r\n\r\n#header {\r\nposition:absolute;\r\ntop:10px;\r\nleft:10px;\r\nright:10px;\r\nwidth:auto;\r\n}\r\n\r\n#page {\r\nposition:absolute;\r\ntop:50px;\r\nleft:10px;\r\nright:10px;\r\nbottom:10px;\r\nwidth:auto;\r\nheight:auto;\r\n}\r\n\r\n#page_title {\r\nposition:absolute;\r\ntop:0px;\r\nleft:0px;\r\n}\r\n\r\n#user_info {\r\nposition:absolute;\r\ntop:0px;\r\nright:0px;\r\nwidth:22%;\r\npadding:8px 1% 8px 1.5%;\r\n}\r\n\r\n#main {\r\nposition:absolute;\r\ntop:0px;\r\nleft:0px;\r\nbottom:0px;\r\nheight:auto;\r\n}\r\n\r\n#flash_message {\r\npadding:8px;\r\nheight:14px;\r\ntext-align:center;\r\n}\r\n\r\n#main_inner {\r\nposition:absolute;\r\ntop:0px;\r\nleft:0px;\r\nbottom:0px;\r\nheight:auto;\r\nwidth:100%;\r\n}\r\n\r\n#toolbar {\r\nposition:absolute;\r\ntop:0px;\r\nright:0px;\r\nbottom:0px;\r\nheight:auto;\r\npadding-top:10px;\r\npadding-bottom:10px;\r\noverflow:hidden;\r\n}\r\n#toolbar_expand {\r\nposition:absolute;\r\ntop:0px;\r\nbottom:0px;\r\nwidth:0.5%;\r\nheight:auto;\r\ntext-align:center;\r\n}\r\n#toolbar_expand table {\r\nheight:100%;\r\nwidth:100%;\r\n}\r\n\r\n#inputs {\r\nwidth:100%;\r\n}\r\n#inputs td {\r\nheight:22px;\r\n}\r\n#inputs label {\r\ndisplay:inline;\r\nvertical-align:middle;\r\n}\r\n#inputs input {\r\nwidth:100%;\r\nmargin-bottom:1px;\r\n}\r\n#inputs_field_column {\r\nwidth:100%;\r\npadding:0 8px 0 10px;\r\n}\r\n#faux_inputs_field {\r\nwidth:100%;\r\npadding:3px;\r\npadding-bottom:2px;\r\ncursor:text;\r\n}\r\n\r\n#main-field {\r\nposition:absolute;\r\ntop:30px;\r\nleft:0px;\r\nright:8px;\r\nbottom:48px;\r\nwidth:auto;\r\nheight:auto;\r\n}\r\n#main-field textarea {\r\nwidth:100%;\r\nheight:100%;\r\n}\r\n\r\n#save-toolbar {\r\nposition:absolute;\r\nbottom:0px;\r\nleft:0px;\r\nwidth:100%;\r\nheight:30px;\r\ntext-align:right;\r\n}\r\n\r\n#dialog {\r\ndisplay:none;\r\n}\r\n\r\n#tooltip {\r\ndisplay:none;\r\nposition:absolute;\r\npadding:2px;\r\nz-index:10000;\r\nmax-width:200px;\r\n}'),(5,'mda000000014hp6llanjovqz074stxmd','text/javascript','[\"set_movabl_url\",\"meta_fields_url\",\"get_meta_url\",\"set_meta_url\",\"delete_movabl_url\",\"get_movabl_field_url\",\"quicksearch_url\",\"get_index_url\"]','//Global Functions\nmouseX = 0;\nmouseY = 0;\n\n//Helper functions\nfunction oc(a) {\n	var o = {};\n	for(var i=0;i<a.length;i++) {\n		o[a[i]]=\'\';\n	}\n	return o;\n}\n\nfunction highlightTables () {\n\n	$(\'table.data\').each(function () {\n		$(this).children(\'tbody\').children(\'tr:even\').removeClass(\'odd\').addClass(\'even\');\n	});\n	$(\'table.data\').each(function () {\n		$(this).children(\'tbody\').children(\'tr:odd\').removeClass(\'even\').addClass(\'odd\');\n	});\n	\n}\n$(window).load(highlightTables);\n\nfunction fullDialogSize () {\n\n	var w = $(window).width() * 0.8;\n	var h = $(window).height() * 0.8;\n	if (w/h > 3/2)\n		var width = Math.floor(h * 3/2);\n	else\n		var width = w;\n	return {\n		\'width\' : width,\n		\'height\' : Math.floor(width * 2/3)\n	}\n\n}\n\n//Main window tabs\nMovablsTabs = {\n	\n	container : null,\n	tab_template : null,\n	tab_container : null,\n	tabs : {},\n	current_tab : null,\n	keys : [],\n	tab : function () {},\n	\n	init : function() {\n	\n		this.container = $(\'#window_container\');\n		this.tab_template = $(\'#window_tab_container\').html();\n		this.tab_container = $(\'#window_tab_container\')\n						.text(\'\')\n						.sortable({\n							\'axis\' : \'x\',\n							\'revert\' : true,\n							\'start\' : function() {\n								MovablsTabs.removeEvents();\n							},\n							\'stop\' : function() {\n								MovablsTabs.initEvents();\n							}\n						})\n						.disableSelection();\n		$(\'#movabls_logo\').click(MovablsTabs.dashboard);\n		this.dashboard();\n		\n	},\n	\n	add : function(label, url) {\n	\n		//If URL is already open, just open that tab\n		for (var i in this.tabs) {\n			if (url == this.tabs[i].iframe_element.attr(\'src\')) {\n				this.tabs[i].open();\n				return;\n			}\n		}\n		\n		var key = \'tab_\' + this.keys.length;\n		this.keys.push(key);\n		\n		this.tabs[key] = new this.tab();\n		this.tabs[key].key = key;\n		\n		this.tab_container.append(this.tab_template);\n		this.tabs[key].tab_element = $(\'#tab_x\');\n		$(\'#tab_x\')\n			.attr(\'id\',key)\n			.children(\'.window_tab_label\')\n				.text(label);\n				\n		this.container.append(\'<iframe id=\"window_content_\'+key+\'\" frameborder=\"0\" class=\"window_content\" src=\"\'+url+\'\"></iframe>\');\n		this.tabs[key].iframe_element = $(\'#window_content_\'+key);\n		\n		this.initEvents();\n		this.tabs[key].open();\n		\n	},\n	\n	dashboard : function() {\n	\n		MovablsTabs.closeAll();\n		MovablsTabs.current_tab = null;\n		$(\'#window_content_0\').css(\'z-index\',\'10000\');\n		$(\'#movabls_logo img\').css(\'top\',\'-27px\');\n	\n	},\n	\n	closeAll : function() {\n	\n		$(\'#window_content_0\').css(\'z-index\',\'1\');\n		$(\'#movabls_logo img\').css(\'top\',\'0px\');\n		\n		for (var i in this.tabs)\n			this.tabs[i].close();\n	\n	},\n	\n	renameCurrent : function(label) {\n	\n		this.tabs[this.current_tab].tab_element.children(\'.window_tab_label\').text(label);\n	\n	},\n	\n	removeCurrent : function(confirm) {\n	\n		this.tabs[this.current_tab].remove();\n		if (confirm != undefined) {\n			//Pass confirmation message to dashboard window\n			var ifrm = document.getElementById(\'window_content_0\');\n			ifrm = (ifrm.contentWindow) ? ifrm.contentWindow : (ifrm.contentDocument.document) ? ifrm.contentDocument.document : ifrm.contentDocument;\n			ifrm.FlashMessage.displayConfirmation(confirm);\n		}\n		\n	},\n	\n	initEvents : function () {\n	\n		$(\'.window_tab\').click(function () {\n			MovablsTabs.tabs[$(this).attr(\'id\')].open();\n		});\n		$(\'.window_tab\').children(\'.window_tab_close\').click(function () {\n			MovablsTabs.tabs[$(this).parent().attr(\'id\')].remove();\n		});\n	\n	},\n	\n	removeEvents : function () {\n	\n		$(\'.window_tab\').unbind(\'click\');\n		$(\'.window_tab\').children(\'.window_tab_close\').unbind(\'click\');\n	\n	}\n	\n}\nMovablsTabs.tab.prototype = {\n\n	key : null,\n	tab_element : null,\n	iframe_element : null,\n	\n	open : function() {\n	\n		MovablsTabs.closeAll();\n		this.tab_element.addClass(\'ui-state-active\');\n		this.iframe_element.css(\'z-index\',\'10000\');\n		MovablsTabs.current_tab = this.key;\n	\n	},\n	\n	close : function() {\n		\n		this.tab_element.removeClass(\'ui-state-active\');\n		this.iframe_element.css(\'z-index\',\'1\');\n	\n	},\n	\n	remove : function() {\n	\n		if (this.tab_element.hasClass(\'ui-state-active\'))\n			MovablsTabs.dashboard();\n		delete MovablsTabs.tabs[this.key];\n		this.tab_element.remove();\n		this.iframe_element.remove();\n	\n	}\n	\n}\n//Wrapper function to link-to or open a parent\'s tab\nfunction link (label,url) {\n	if (window.location != window.parent.location)\n		parent.MovablsTabs.add(label,url);\n	else {\n		var newWindow = window.open(url, \'_blank\');\n		newWindow.focus();\n	}	\n	return false;	\n}\n\n//Toolbar\nMovablsToolbar = {\n\n	open : function (speed) {\n	\n		if (speed == undefined)\n			speed = 0;\n		\n		$(\'#toolbar\')\n			.css(\'padding-left\',\'1%\')\n			.css(\'padding-right\',\'1%\')\n			.animate({\n				width : \'22%\',\n			}, speed);\n		$(\'#toolbar_expand\').animate({\n			right : \'24%\'\n		}, speed);\n		$(\'#main\').animate({\n			width : \'74%\'\n		}, speed, function () {\n			$(\'#toolbar_inner\').show();\n		});\n		$(\'#toolbar_expand td\').text(\'>\');\n	\n	},\n	\n	close : function (speed) {\n	\n		if (speed == undefined)\n			speed = 0;\n		\n		$(\'#toolbar_inner\').hide();\n		$(\'#toolbar\')\n			.css(\'padding-left\',\'0\')\n			.css(\'padding-right\',\'0\')\n			.animate({\n				width : \'0px\'\n			}, speed);\n		$(\'#toolbar_expand\').animate({\n			right : \'0px\'\n		}, speed);\n		$(\'#main\').animate({\n			width : \'98%\'\n		}, speed);\n		$(\'#toolbar_expand td\').text(\'<\');\n	\n	},\n	\n	toggle : function () {\n	\n		if (parseFloat($(\'#main\').css(\'width\')) > 75)\n			MovablsToolbar.open(500);\n		else\n			MovablsToolbar.close(500);\n	\n	}\n\n}\n$(window).load(function () {\n	$(\'#toolbar_expand\').click(MovablsToolbar.toggle);\n});\n\n//Tooltip\nTooltip = {\n\n	text : \'\',\n	showTimer : null,\n	hideTimer : null,\n	\n	show : function () {\n	\n		var xpos = mouseX;\n		var ypos = mouseY + 21;\n		$(\'#tooltip\').html(Tooltip.text);\n		$(\'#tooltip\')\n			.css(\'top\',ypos+\'px\')\n			.css(\'left\',xpos+\'px\')\n			.show();\n		Tooltip.hideTimer = setTimeout(function () {\n			Tooltip.hide();\n		}, 4000);\n	\n	},\n	\n	hide : function () {\n	\n		$(\'#tooltip\').hide();\n		clearTimeout(Tooltip.showTimer);\n		clearTimeout(Tooltip.hideTimer);\n		Tooltip.showTimer = null;\n		Tooltip.hideTimer = null;\n		Tooltop.text = \'\';\n	\n	}\n\n}\n$.fn.tooltip = function (text) {\n	\n	$(this).unbind(\'mouseenter mouseleave\');\n	if (text != undefined && text.length > 0) {\n		$(this).hover((function (text) {\n			return function () {\n			\n				Tooltip.text = text;\n				Tooltip.showTimer = setTimeout(function () {\n					Tooltip.show();\n				}, 500);\n				\n			}\n		})(text), \n			Tooltip.hide\n		);\n	}\n	\n}\n\n//Flash Message\nFlashMessage = {\n	\n	reset : function () {\n	\n		$(\'#flash_message\').hide().removeClass(\'flash-message-confirmation flash-message-notice flash-message-error\');\n	\n	},\n	\n	displayConfirmation : function (html) {\n	\n		this.reset();\n		$(\'#flash_message\').addClass(\'flash-message-confirmation\');\n		this.display(html);\n	\n	},\n	\n	displayNotice : function (html) {\n	\n		this.reset();\n		$(\'#flash_message\').addClass(\'flash-message-notice\');\n		this.display(html);\n	\n	},\n	\n	displayError : function (html) {\n	\n		this.reset();\n		$(\'#flash_message\').addClass(\'flash-message-error\');\n		this.display(html);\n	\n	},\n	\n	display : function (html) {\n		\n		$(\'#flash_message\').html(html);\n		$(\'#main_inner\').css(\'top\',\'40px\');\n		$(\'#flash_message\').fadeIn(500);\n		setTimeout(this.hide,4000);\n		\n	},\n	\n	hide : function () {\n	\n		FlashMessage.reset();\n		$(\'#main_inner\').animate({\n			top : \'0px\'\n		},500);\n	\n	}\n	\n}\n\n//Server Interaction Functions\nMovablsAPI = {\n\n	saveMedia : function (GUID) {\n	\n		var data = {\n			inputs : MovablsInputs.data,\n			mimetype : $(\'#media_type\').val(),\n			content : $(\'#main-field textarea\').val()\n		}\n		json = JSON.stringify(data);\n		\n		MovablsAPI.saveMovabl(\'media\', GUID, json);\n		\n	},\n	\n	saveFunction : function (GUID) {\n	\n		var data = {\n			inputs : MovablsInputs.data,\n			content : $(\'#main-field textarea\').val()\n		}\n		json = JSON.stringify(data);\n		\n		MovablsAPI.saveMovabl(\'function\', GUID, json);\n	\n	},\n	\n	sa